mapper.select:
select * from (
	select * from (
		select rownum as rn, q.* from ( ( select * from view_qna where title like '%'||#{param2}||'%' or content like '%'||#{param2}||'%' or name like '%'||#{param2}||'%' order by qna_num desc ) q )
	) where rn >= #{param1.startNum}
) where rn <= #{param1.endNum}
